import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import moment from 'moment';
import _ from 'lodash';
import { css, StyleSheet } from 'aphrodite';
import { Container, Button, Grid } from 'semantic-ui-react';

import { COLORS, ICONS, WINDOW_DIMENSIONS } from '../../../../constants';
import verifiedPartnerIcon from '../../../../images/icons/verified_partner_icon.svg';
import heartIcon from '../../../../images/icons/heart.svg';
import filledHeartIcon from '../../../../images/icons/filled_heart.svg';
import backgroundImage from '../../../../images/backgrounds/droplet-background.svg';
import beanImage from '../../../../images/backgrounds/bean.svg';
import haloLogo from '../../../../images/logos/halo_logo.svg';
import {
  saveRFP as saveRFPAction,
  unsaveRFP as unsaveRFPAction,
  updateCurrentRFP as updateCurrentRFPAction,
} from '../../actions/defaultActions';
import {
  toggleLoginModal as toggleLoginModalAction,
} from '../../actions/profileActions';
import { transformText } from '../../utils/textUtils';

import InviteFacultyModal from './InviteFacultyModal';
import ComingSoonModal from './ComingSoonModal';
// import WelcomeUniversityModal from './WelcomeUniversityModal';

const { TABLET_LANDSCAPE_MEDIA_QUERY, MOBILE_MEDIA_QUERY } = WINDOW_DIMENSIONS;

const styles = StyleSheet.create({
  RFPPage_container: {
    width: '85%',
    margin: 'auto',
    zIndex: '1000',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      width: '90%',
    },
    [MOBILE_MEDIA_QUERY]: {
      width: '100%',
      padding: '16px'
    },
  },
  RFPPage_company: {
    background: COLORS.haloGray,
    height: '511px',
    position: 'relative',
    overflow: 'hidden',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      paddingLeft: '30px',
      textAlign: 'left',
      height: '450px',
    },
    [MOBILE_MEDIA_QUERY]: {
      padding: '0 24px',
      background: COLORS.white,
      height: 'unset',
    },
  },
  RFPPage_companyLink: {
    color: COLORS.haloBlack,
    fontSize: '20px',
    lineHeight: '24px',
    fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif',
    [MOBILE_MEDIA_QUERY]: {
      display: 'block',
      fontSize: '16px',
    },
  },
  RFPPage_title: {
    color: COLORS.haloBlack,
    fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif',
    fontWeight: 'bold',
    fontSize: '36px',
    lineHeight: '48px',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      fontSize: '24px',
      lineHeight: '24px',
      marginTop: '20px',
      marginBottom: '20px',
    },
    [MOBILE_MEDIA_QUERY]: {
      fontWeight: '600',
      fontSize: '24px',
      lineHeight: '36px',
      margin: '5px 0'
    },
  },
  RFPPage_summary: {
    color: COLORS.haloBlack,
    fontSize: '20px',
    lineHeight: '30px',
    marginTop: '50px',
    marginBottom: '0px',
    [MOBILE_MEDIA_QUERY]: {
      fontSize: '16px',
    },
  },
  RFPPage_bulletList: {
    marginLeft: '15px',
    marginBottom: '2em',
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
  },
  SubmitProposal_bulletPoint: {
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
    [MOBILE_MEDIA_QUERY]: {
      fontSize: '14px',
    },
  },
  RFPPage_regularFont: {
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
    marginBottom: '2em',
    [MOBILE_MEDIA_QUERY]: {
      fontSize: '14px',
    },
  },
  RFPPage_miniSectionHeader: {
    fontWeight: 'bold',
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
    marginBottom: '20px',
  },
  RFPPage_deadline: {
    fontFamily: '-apple-system, BlinkMacSystemFont, sans-serif',
    fontWeight: 'bold',
    fontSize: '14px',
    lineHeight: '24px',
    textAlign: 'center',
    textTransform: 'uppercase',
    color: COLORS.lightGray,
    marginBottom: '16px',
  },
  RFPPage_submitButton: {
    width: '300px',
    maxWidth: '90%',
    height: '48px',
    background: COLORS.lightBlue,
    backgroundImage: 'linear-gradient(134.72deg, #4E9DF5 0%, #48B2F4 100%)',
    border: `1px solid ${COLORS.lightBlue}`,
    borderRadius: '4px',
    padding: '8px 30px',
    color: COLORS.white,
    lineHeight: '15px',
    fontWeight: 500,
    fontSize: '16px',
    [MOBILE_MEDIA_QUERY]: {
      width: '100%',
